// Generated by CoffeeScript 1.6.3
App.HomeView = App.BaseView.extend({
  constructor: function(geo) {
    this.geo = geo;
    return Backbone.View.apply(this, arguments);
  },
  initialize: function() {
    this.dishCollection = new App.DishCollection(this.geo);
    this.dishCollection.on('reset', this.onDishReset, this);
    return this.dishCollection.fetch({
      reset: true
    });
  },
  onDishReset: function(dishcol) {
    _.each(dishcol.models, function(dish) {
      var view;
      view = new App.DishView({
        model: dish
      });
      return this.$el.find('.carousel-inner').append(view.render().el);
    }, this);
    return this.$el.find('.item').first().addClass('active');
  },
  template: App.templates['home']
});
